INSERT [dbo].[product] ([ID], [catid], [Submenu], [name], [slug], [img], [detail], [number], [price], [pricesale], [metakey], [metadesc], [created_at], [created_by], [updated_at], [updated_by], [status], [sold]) VALUES (27, 23, 1, N'iPhone 14 Pro Max 128GB', N'iphone-14-pro-max-128gb', N'cpu-bovixuly/iphone-14-pro-max-128gb.jpeg', N'<h1 class="txt-24" style="margin: 10px 0px 0px; font-size: 24px; font-family: Arial, Helvetica, sans-serif; line-height: 1.1; color: rgb(51, 51, 51);"><div id="owl-feature" style="font-size: 14px;"><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;"><strong style="font-weight: bold;">Đánh giá iPhone 14 Pro Max - chiếc smartphone “đỉnh nhất" nhà Táo!</strong></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;"><strong style="font-weight: bold;">Ngoại hình thời thượng với hơn 4 lựa chọn màu sắc</strong></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Những năm gần đây, Apple luôn quan tâm đến việc cải tiến các tùy chọn màu sắc của iPhone. Công bố chính thức trên website của nhà Táo vào ngày 7/9 vừa qua, phiên bản iPhone 14 Pro Max khoác lên mình nhiều gam màu mới mẻ nhằm đáp ứng nhu cầu đa dạng của người dùng.&nbsp;</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Bên cạnh những phiên bản màu sắc quen thuộc là đen, bạc, vàng, iPhone 14 Pro Max còn được trang bị thêm gam màu tím cực kỳ trẻ trung, ngọt ngào. Điểm đặc biệt là mẫu iPhone màu tím này có thể thay đổi tông màu theo điều kiện ánh sáng xung quanh.&nbsp;</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: center;"><span style="font-family: Arial;"><img alt="" src="https://imgs.viettelstore.vn/Images/Product/ProductImage/dien-thoai/Apple/iPhone%2014%20Pro%20Max%20128/iPhone-14-Pro-Max-1.jpg" style="border: 0px;"></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: center;"><span style="font-family: Arial;"><em>Hệ màu sắc cơ bản của iPhone 14 Pro Max</em></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Ở những phiên bản iPhone 12 và iPhone 12 mini, màu tím cũng từng được "nhà Táo" áp dụng trong đợt giảm giá kích cầu mua sắm. Ngoài ra, các dòng sản phẩm khác như iMac 24 inch, iPad mini thế hệ 6 hay iPad Air thế hệ 5 với tùy chọn màu tím đều nhận được phản hồi tích cực khi ra mắt. Chính vì thế, "Táo khuyết" quyết định tiếp nối xu hướng mới lạ này cho thế hệ iPhone 14 năm 2022.&nbsp;</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;"><strong style="font-weight: bold;">Cụm camera cảm biến 48MP và quay video 4K chất lượng cao</strong></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">iPhone 14 Pro Max sở hữu camera chính 48MP với cảm biến 4 điểm ảnh tiên tiến hỗ trợ rất nhiều cho việc chụp ảnh trong điều kiện thiếu sáng. Với cảm biến quad-pixel, bạn có thể tạo thêm một ống kính tele 2x để mở rộng các tùy chọn thu phóng, cung cấp hình ảnh và video có độ phân giải cao mà không cần thu phóng kỹ thuật số.&nbsp;</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: center;"><span style="font-family: Arial;"><img alt="" src="https://imgs.viettelstore.vn/Images/Product/ProductImage/dien-thoai/Apple/iPhone%2014%20Pro%20Max%20128/iPhone-14-Pro-Max-2.jpg" style="border: 0px;"></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: center;"><span style="font-family: Arial;"><em>Cụm camera cảm biến 48MP của iPhone 14 Pro Max</em></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Hơn nữa, khả năng quay video 4K đem đến trải nghiệm quay phim chất lượng cao, độc đáo cho người dùng. Sự kết hợp của ống kính tele chất lượng quang học gấp 2 lần, đèn flash True Tone với tính đồng nhất gấp ba và camera trước True Depth là công cụ hỗ trợ đắc lực, mang lại những bức hình và video chân thực, sống động bằng chính chiếc iPhone của bạn.&nbsp;</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;"><strong style="font-weight: bold;">Thiết kế hình viên thuốc thay thế “notch tai thỏ”</strong></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Nhà Táo khuyết đã thực hiện một bước cải tiến đột phá về thiết kế khi loại bỏ hoàn toàn phần notch tai thỏ trên các phiên bản iPhone 14 Pro và Pro Max. Thay vào đó, Apple áp dụng thiết kế hình viên thuốc chứa camera TrueDepth cung cấp cảm biến Face ID và tính năng lấy nét tự động.</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: center;"><span style="font-family: Arial;"><img alt="" src="https://imgs.viettelstore.vn/Images/Product/ProductImage/dien-thoai/Apple/iPhone%2014%20Pro%20Max%20128/iPhone-14-Pro-Max-3.jpg" style="border: 0px;"></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: center;"><span style="font-family: Arial;"><em>Thay thế “notch tai thỏ” bằng thiết kế viên thuốc ấn tượng</em></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Ở khu vực notch, nhà Táo đã thiết kế tính năng Dynamic Island để kết hợp thông báo của iPhone và notch thành một khối thống nhất. Sự cải tiến mới lạ này nhằm đem đến trải nghiệm thị giác liền mạch với những gì được hiển thị trên màn hình cho người dùng.&nbsp;</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Thiết kế mới này đem đến nét sang trọng, thanh tao cho thiết bị, đồng thời tăng diện tích màn hình giúp tối ưu hóa trải nghiệm người dùng. Giao diện khác biệt của iPhone 14 Pro Max đáp ứng sự mong đợi của cộng đồng iFans khi thiết kế “notch tai thỏ” ở phiên bản tiền nhiệm được cho là tốn diện tích và gây bất tiện khi sử dụng.</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;"><strong style="font-weight: bold;">Màn hình Super Retina XDR OLED vô cùng sắc nét</strong></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">So với thế hệ trước, kích thước màn hình của iPhone 14 Pro Max vẫn là 6.7 inch. Tuy nhiên, Apple trang bị Super Retina XDR OLED với màu sắc 1B, kích thước 6.1 inch cùng độ phân giải 2778 x 1284 pixel và mật độ điểm ảnh khoảng 459 ppi cho màn hình mẫu iPhone mới. Điều này không chỉ cải thiện độ tương phản, đem lại độ sáng cao mà còn tiết kiệm pin tối đa, giúp người dùng trải nghiệm màn hình nét căng khi sử dụng.</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: center;"><span style="font-family: Arial;"><img alt="" src="https://imgs.viettelstore.vn/Images/Product/ProductImage/dien-thoai/Apple/iPhone%2014%20Pro%20Max%20128/iPhone-14-Pro-Max-5.jpg" style="border: 0px;"></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: center;"><span style="text-align: justify; font-family: Arial;"><em>iPhone 14 Pro Max được trang bị màn hình Super Retina XDR OLED với màu sắc 1B sắc nét</em></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Điểm đặc biệt là màn hình Pro Max được phủ lớp oleophobic chống bám vân tay để thuận tiện hơn khi dùng. Với công nghệ True Tone Wide color (p3) và Haptic Touch, tính năng HDR display còn đem đến những bức hình chất lượng độc đáo kể cả trong điều kiện có độ tương phản cao.&nbsp;</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;"><strong style="font-weight: bold;">Tốc độ truyền tải dữ liệu cực nhanh nhờ chip 5G</strong></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">iPhone 14 Pro Max sử dụng chip 5G với hiệu suất mạnh mẽ giúp đẩy nhanh tốc độ truyền tải dữ liệu. Bộ vi xử lý tần số vô tuyến (RF) 5G kết hợp cùng modem Snapdragon X65 nhằm tạo ra hệ thống kết nối mạng 5G tân tiến nhất cho dòng iPhone 14 Pro Max. Tốc độ truyền tải dữ liệu lên đến 10Gb/giây đem đến trải nghiệm tuyệt vời cho người dùng.</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Hơn thế nữa, điện thoại Pro Max sẽ vừa có hiệu suất cao ở băng tần 5G sub 6 GHz lại vừa tiết kiệm điện năng nhờ ứng dụng công nghệ sản xuất 6nm cho chip RF 5G. Việc sử dụng modem Snapdragon X65 làm tăng phạm vi phủ sóng mmWave tốt hơn rất nhiều và hỗ trợ tất cả tần số mmWave thương mại trên toàn cầu.</span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;"><strong style="font-weight: bold;">Tính năng an toàn mới cực kỳ độc đáo</strong></span></p><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Điện thoại iPhone 14 Pro Max được nhà Apple trang bị tính năng an toàn mới với kết nối vệ tinh và phát hiện va chạm.&nbsp;</span></p><ul style="margin-bottom: 10px;"><li style="text-align: justify;"><span style="font-family: Arial;">Kết nối vệ tinh: có khả năng kết nối vệ tinh cao hơn, dễ dàng bắt sóng vệ tinh mặt đất, gửi tín hiệu khẩn cấp khi người dùng gặp nguy hiểm.&nbsp;</span></li></ul><ul style="margin-bottom: 10px;"><li style="text-align: justify;"><span style="font-family: Arial;">Phát hiện va chạm: Nếu không may gặp phải bất kỳ va chạm nào, iPhone sẽ tự động phát tín hiệu báo nguy hiểm tới người dùng. Tuy nhiên, tính năng này chỉ hỗ trợ khi sử dụng iPhone 14 Pro Max tại Mỹ.</span></li></ul><p style="margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;"><span style="font-family: Arial;">Nhờ sự kết hợp những tính năng thông minh này mà người dùng được đảm bảo an toàn khi sở hữu bên mình.&nbsp;</span></p></div><div id="gioithieu1_viewmore" style="position: absolute; left: 0px; bottom: -9px; right: 0px; z-index: 100; height: 50px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; padding: 20px; text-align: center; color: rgb(40, 138, 214); cursor: pointer; font-size: 14px;"><div class="detail">Xem thêm đặc điểm nổi bật&nbsp;<span class="glyphicon glyphicon-menu-down" style="position: relative; top: 1px; display: inline-block; font-family: &quot;Glyphicons Halflings&quot;; line-height: 1; -webkit-font-smoothing: antialiased;"></span></div></div></h1>', 98, 16990000, 10, N'iPhone 14 Pro Max 128GB', N'iPhone 14 Pro Max 128GB', CAST(N'2022-10-27T14:39:00' AS SmallDateTime), 1, CAST(N'2023-03-22T22:47:00' AS SmallDateTime), 1, 0, 0)
